<?php//find all array keys recursivelyif(!function_exists('array_key_recursive')){	function array_key_recursive($array, $search = false){			if($search !== false){			$list_of_keys = array_keys($input, $search);		}else{			$list_of_keys = array_keys($input);		}				foreach($array as $value){					if(is_array($value)){							if($search !== false){									$list_of_keys = array_merge($list_of_keys, array_key_recursive($value, $search));								}else{								$list_of_keys = array_merge($list_of_keys, array_key_recursive($value));								}						}					}				return $list_of_keys;			}}//Recursive Object to Array//Beware of protected methods, they can only be accessed within their own class, and when outputted, will have *if(!function_exists('object_to_array')){	function object_to_array($obj){		if(is_object($obj)) $obj = (array) $obj;				if(is_array($obj)) {					$new = array();			foreach($obj as $key => $val) {				$new[$key] = object_to_array($val);			}					}else{					$new = $obj;				}				return $new;			}}